
public class Loops {

	public static void main(String[] args) {
		 for(int i=1;i<=10;i++){  
		        System.out.println(i);  
		    } 
		 //while loop
		 int i = 0;
		 while (i < 5) {
		   System.out.println(i);
		   i++;
		 }
		 //for each loop
		 int a[] = {10,20,30,40,50};
		 for(int i : a) {
			 System.out.println(i);
		 }
		 
		// TODO Auto-generated method stub

	}

}
